import React, { useEffect, useState } from 'react';
import { ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import ItemCart from '../../component/ItemCart';
import {addToCart,getCartLocalStorage} from '../../services/Cart';
import { Ionicons } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';

export default function Cart({route,navigation}) {

    const [itemCart, setItemCart] = useState([])

    const fetchData = async () => {
        const response = await getCartLocalStorage();
        return setItemCart(response)
    }

    const handleAddToCart = () => {
        addToCart(route.params).then(()=>{
            fetchData();
        });
    }

    useEffect(() => {        
        fetchData();

        navigation.setOptions({
            headerRight: () => (
            <View>
                <TouchableOpacity 
                style={{ marginRight: 15 }} 
                onPress={()=>{}}>
                    <Ionicons name="ios-trash-bin-outline" size={24} color="black" />
                </TouchableOpacity>
            </View>  
            )
        })
    }
    , [])

    return (
        <View style={styles.container}>

            <TouchableOpacity 
            onPress={handleAddToCart}
            style={styles.container_new_item}>
                <Text style={styles.text_new_item}>
                    Adicionar: {route.params.name}
                </Text>
                <Ionicons 
                name="add-circle-outline" 
                size={30} color="white" 
                style={{position: "absolute", right:15, alignSelf: 'center'}}
                />
            </TouchableOpacity>
            <Text style={styles.title_cart}>ITENS ADICIONADOS</Text>
            <View style={styles.line}></View>
            <ScrollView style={styles.container_item_cart}>
            {itemCart !== null && 
                itemCart.map((item,index)=>(<ItemCart key={index} data={item}/>))
            }
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff'
    },
    title_cart:{
        fontFamily: "PTSans_400Regular",
        fontSize:24,
        marginHorizontal: "1%",
        marginVertical: "1%",
        alignSelf: "center"
    },
    container_new_item:{
        display:"flex",
        flexDirection:"row",
        spaceBetween: "middle",
        alignItems: "center",
        backgroundColor: "#49be25",
        padding: 15,
        marginBottom:10
    },
    text_new_item:{
        marginHorizontal: "1%",
        fontSize:22,
        color: "#fff"
    },
    line:{
        borderTopWidth: 1,
        borderTopColor:"#222"
    }
})
