import 'react-native-gesture-handler';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet } from 'react-native';
import React, { useEffect, useState } from 'react';
import AppLoading from 'expo-app-loading';
import {useFonts, Anton_400Regular} from '@expo-google-fonts/anton';
import Routes from './src/router';
import AsyncStorage from '@react-native-async-storage/async-storage';
import api from 'axios';

export default function App() {

  let [fontsLoaded] = useFonts({
    Anton_400Regular,
  });

  const [user, setUser] = useState({
    access_token: "",
    expires_in: 0,
    scope: "",
    token_type: "bearer",
    userFirstName: "",
    userId: null
  });

  const getUser = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('@user')
      return jsonValue != null ? JSON.parse(jsonValue) : null;
    } catch(e) {
    }
  }

  const saveUser = async (user) => {
    try {
      setUser(user);
      console.log(user)
      return await AsyncStorage.setItem('@user', JSON.stringify(user));
    } catch(e) {
      // save error
    }
  }

  useEffect(
    ()=>verifyUser()
  ,[])

  const verifyUser = () => {
    try{
      getUser().then((userData) => {
        if (userData) {
          api.request({
            method:'POST',
            url: `http://localhost:8080/oauth/token`,
            auth: {
                username: "dscatalog", 
                password: "dscatalog123" 
            },
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            params: {
              grant_type: "password",
              username: user.username || "maria@gmail.com",
              password: user.password || "123456",
            }
          }).then(res => {
              if (res.status >= 200 && res.status < 300) {
                  saveUser(res.data).then(() => console.log('done'))
              } else {

              }
          }).catch((err) => {
            console.log(err);
          })
        }
      })
    } catch (e) {

    }
  }



  if(!fontsLoaded){ 
    return <AppLoading />
  }

  return (
    <>
      <StatusBar style="auto" backgroundColor="#000" translucent={true}/>
      <Routes/>
    </> 
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
