import React, { useEffect, useState } from 'react';
import { Text, View, StyleSheet, ScrollView, Image, TouchableOpacity } from "react-native";
import { Ionicons } from '@expo/vector-icons'; 
import Shoes from '../../component/Shoes';
import {getProducts} from '../../services/Products'
import { useNavigation } from '@react-navigation/native';

export default function Home() {

    const navigation = useNavigation();

    const [products, setProducts] = useState({});

    useEffect(()=>{
        getProducts().then((data) => {
            setProducts(data.content)
        })
    },[])

    return (
        <View style={styles.container}> 
            <View style={styles.header}>
                <Image
                source={require('../../assets/banner.png')}
                style={styles.image}
                />    
                <View style={styles.textContainer}>
                    <Text style={styles.text}>TÊNIS</Text>
                    <Text style={styles.text}>•</Text>
                    <Text style={styles.text}>MASCULINO</Text>
                    <TouchableOpacity style={{position: "absolute", right:0, alignSelf: 'center'}}>
                        <Ionicons 
                            name="filter" 
                            size={24} 
                            color="black" 
                        />
                    </TouchableOpacity>
                </View>
            </View>

            <View style={styles.line}/>

            <ScrollView>
                <Text style={styles.text}>LANÇAMENTOS</Text>
                <View style={{flexDirection: "row", justifyContent: "space-around"}}>
                    <Shoes img={require('../../assets/1.png')} cost="R$90,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia
                    </Shoes>
                    <Shoes img={require('../../assets/2.png')} cost="R$140,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia Vagabunda
                    </Shoes>
                </View>
                <View style={{flexDirection: "row", justifyContent: "space-around"}}>
                    <Shoes img={require('../../assets/3.png')} cost="R$90,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia
                    </Shoes>
                    <Shoes img={require('../../assets/4.png')} cost="R$140,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia Vagabunda
                    </Shoes>
                </View>
                <View style={{flexDirection: "row", justifyContent: "space-around"}}>
                    <Shoes img={require('../../assets/5.png')} cost="R$90,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia
                    </Shoes>
                    <Shoes img={require('../../assets/6.png')} cost="R$140,90" onClick={()=>navigation.navigate("Detail")}>
                        Nike Air Max Dia Vagabunda
                    </Shoes>
                </View>
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        width:"100%",
        backgroundColor:"#fff"
    },
    header:{
        marginBottom: 8
    },
    image:{
        width:"100%"
    },
    textContainer:{
        flexDirection: "row",
        marginVertical: "5%",
        marginHorizontal: "5%"
    },
    text:{
        fontFamily: "Anton_400Regular",
        fontSize:26,
        marginHorizontal: "1%"
    }
})
