import React, { useEffect, useState } from 'react';
import { Text, View, StyleSheet, ScrollView, Image, TouchableOpacity, FlatList } from "react-native";
import { Ionicons, AntDesign } from '@expo/vector-icons'; 
import Product from '../../component/Product';
import {getProducts} from '../../services/Products'
import { useNavigation } from '@react-navigation/native';

export default function Home({navigation}) {

    const [products, setProducts] = useState({});
    const [loadingMore, setLoadingMore] = useState(false);

    useEffect(()=>getProducts(4).then((data) => setProducts(data.content)),[])

    const ListFooterComponent = () => {
        <AntDesign size={24} />
    }

    const renderItem = ({ item }) => {
        return <Product styles={styles.text} data={item} >{item.name}</Product>
    };

    let stopFetchMore = true;

    const handleOnEndReached = async () => {
        setLoadingMore(true);
        if (!stopFetchMore) {
            const res = await getProducts(res.numberOfElements+3).then((data) => setProducts(data.content))
            if (res.last) return setLoadingMore(false);
            setProducts([...products, ...res.content])
            stopFetchMore = true;
        }
        setLoadingMore(false);
    }

    return (
        <View style={styles.container}> 
            <View style={styles.header}>
                <Image
                source={require('../../assets/banner.png')}
                style={styles.image}
                />    
                <View style={styles.textContainer}>
                    <Text style={styles.text}>LOJA</Text>
                    <Text style={styles.text}>•</Text>
                    <Text style={styles.text}>BELLAFACE</Text>
                    <TouchableOpacity style={{position: "absolute", right:0, alignSelf: 'center'}}>
                        <Ionicons 
                            name="filter" 
                            size={24} 
                            color="black" 
                        />
                    </TouchableOpacity>
                </View>
            </View>

            <View style={styles.line}/>

            <View>
                <Text style={styles.text}>LANÇAMENTOS</Text>
                <FlatList 
                    data={products}
                    renderItem={renderItem}
                    keyExtractor={item => item.id}
                    horizontal={true}
                    onEndReached={handleOnEndReached}
                    onEndReachedThreshold={0.5}
                    onScrollBegin={()=>{
                        stopFetchMore = false;
                    }}
                    ListFooterComponent={() => loadingMore && <ListFooterComponent/>}
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        width:"100%",
        backgroundColor:"#fff"
    },
    header:{
        marginBottom: 8
    },
    image:{
        width:"100%"
    },
    textContainer:{
        flexDirection: "row",
        marginVertical: "5%",
        marginHorizontal: "5%"
    },
    text:{
        fontFamily: "Anton_400Regular",
        fontSize:26,
        marginHorizontal: "1%"
    }
})
